import React, { useState } from 'react';

import {
  SafeAreaView,
  StyleSheet,
  Text,
  View,
  TouchableHighlight,
  Modal,
  Alert,
  Pressable
} from 'react-native';

//importing library to use Stopwatch and Timer
import { Timer } from 'react-native-stopwatch-timer';

let startDate = null;
let stopDate = null;

function sendTimerInfos() {
  const startDateFormated = new Date(startDate);
  const stopDateFormated = new Date(stopDate);
  const timeSpent = Math.floor(Math.abs(startDateFormated - stopDateFormated) / 600) / 100;
  console.log(timeSpent); // Time spent is in Minutes
}

function handleTimer(isTimerStart) {
  if (!isTimerStart)
    startDate = new Date();
  else {
    stopDate = new Date();
    sendTimerInfos();
  }
}

export default function TimerModule({ mode, style, ...props }) {
  const [isTimerStart, setIsTimerStart] = useState(false);
  const [timerDuration, setTimerDuration] = useState(1200000);
  const [resetTimer, setResetTimer] = useState(false);
  const [modalVisible, setModalVisible] = useState(false);

  return (
        <View>
          <View style={styles.centeredView}>
            <Modal
              animationType="slide"
              transparent={true}
              visible={modalVisible}
              onRequestClose={() => {
                Alert.alert("Modal has been closed.");
                setModalVisible(!modalVisible);
              }}
            >
              <View style={styles.centeredView}>
                <View style={styles.modalView}>
                  <Text style={styles.modalText}>Congratulation ! A Verifiable Credential has been generated ! </Text>
                  <Pressable
                    style={[styles.button, styles.buttonClose]}
                    onPress={() => setModalVisible(!modalVisible)}
                  >
                    <Text style={styles.textStyle}>close</Text>
                  </Pressable>
                </View>
              </View>
            </Modal>
          </View>
          <Timer
            totalDuration={timerDuration}
            //msecs
            //Time Duration
            start={isTimerStart}
            //To start
            reset={resetTimer}
            //To reset
            options={options}
            //options for the styling
            handleFinish={() => {
              alert('Custom Completion Function');
            }}
            //can call a function On finish of the time
            getTime={(time) => {
            }}
          />
          <TouchableHighlight style={styles.sectionStyle}
            onPress={() => {
              setIsTimerStart(!isTimerStart);
              setResetTimer(false);
              handleTimer(isTimerStart);
              setModalVisible(isTimerStart);
            }}>
            <Text style={styles.buttonText}>
              {!isTimerStart ? 'START' : 'STOP'}
            </Text>
          </TouchableHighlight>
          <TouchableHighlight style={styles.sectionStyle}
            onPress={() => {
              setIsTimerStart(false);
              setResetTimer(true);
            }}>
            <Text style={styles.buttonText}>RESET</Text>
          </TouchableHighlight>
        </View>
  );
};

const styles = StyleSheet.create({
  title: {
    textAlign: 'center',
    fontSize: 20,
    fontWeight: 'bold',
    padding: 20,
  },
  sectionStyle: {
    alignItems: 'center',
    justifyContent: 'center',
  },
  buttonText: {
    fontSize: 20,
    marginTop: 10,
    color: '#2a4876',
  },
  centeredView: {
    flex: 1,
    justifyContent: "center",
    alignItems: "center",
    marginTop: 22
  },
  modalView: {
    margin: 20,
    backgroundColor: "white",
    borderRadius: 20,
    padding: 35,
    alignItems: "center",
    shadowColor: "#000",
    shadowOffset: {
      width: 0,
      height: 2
    },
    shadowOpacity: 0.25,
    shadowRadius: 4,
    elevation: 5
  },
  button: {
    borderRadius: 20,
    padding: 10,
    elevation: 2
  },
  buttonClose: {
    backgroundColor: "#2196F3",
  },
  textStyle: {
    color: "white",
    fontWeight: "bold",
    textAlign: "center"
  },
  modalText: {
    marginBottom: 15,
    textAlign: "center"
  }
});

const options = {
  container: {
    borderRadius: 25,
    borderWidth: 2,
    borderColor: '#2a4876',
    borderStyle: 'solid',
    padding: 5,
    width: 200,
    justifyContent: 'center',
    alignItems: 'center',
    alignSelf: 'center',
    color: 'black'
  },
  text: {
    fontSize: 35,
    color: '#2a4876',
    marginLeft: 7,
  }
};
